// Load modules

var Joi = require('joi');

exports.options = Joi.alternatives().try(Joi.string(), Joi.object().keys({
	datetime: Joi.string().required(),
	usertype: Joi.any().valid('user', 'apikey').required(),
	username: Joi.string().required(),
	user_id: Joi.string().required(),
	account: Joi.string().required(),
	account_id: Joi.string().required(),
	activity_type: Joi.any().valid('add', 'modify', 'delete', 'publish', 'purge').required(),
	activity_target: Joi.string().required(),
	target_name: Joi.string().required(),
	target_id: Joi.string().required(),
	operation_status: Joi.any().valid('success', 'failure').required(),
	target_object: Joi.object().required(),
}));